def generate_valid_date():
    rand_year= random.randint(2000, 2050)
    rand_month= random.randint(1, 12)
    rand_day= random.randint(1, 31)
    res= str(rand_year) + "-" + str(rand_month) + "-" + str(rand_day)
    return res

